<?require_once '../classes/_Var.php';$Var        =_Var::getInstance();  $Var->__autoload("Script","../classes/Data"); $Script=Script::getInstance();$Data=Data::getInstance();$Script->object("Data");$Script->attribute("CLRMN               : '".$Data->getParameter("CLR_MIN")."',");$Script->attribute("CLRTM               : '".$Data->getParameter("CLR_TIME")."',");$Script->attribute("DFT_TM              : '".$Data->getParameter("DEFAULT_TIME")."',");$Script->attribute("DFT_TMC             : '".$Data->getParameter("DEFAULT_TIME_CONTINUOUS")."',");$Script->attribute("GUI_VALIDATE        : '".$Data->getParameter("GUI_VALIDATE")."',");$Script->send("search","layerA,layerB,table,field,value,optionList",                            'Utility.loading();               Request.setVar("LAYERB",layerB);',                            "POST",                            "filing.php",                            '"option="       + "search" +               "&table="       + table +                "&field="       + field +               "&idInput="     + Request.getVar("LAYERB") +                "&value="       + value +               "&optionList="  + optionList,',                            'setTimeout(function() {                            Request.html(layerA,html);                          Request.initialize();                          Utility.close();                          }, Data.DFT_TM);',true);$Script->method("table","name",                '$("#"+name+"").dataTable( {          					"bAutoWidth": false,          					"aaSorting": [[ 5, "asc" ]],          					"aoColumns": [null,null,null,null,null]          				} );          				$("#"+name+"").css("width","100%");',true);$Script->method("checkAll","id",                '$("input[name="+id+"]").change(function(){		           $("input[type=checkbox]").each( function() {						         if($("input[name="+id+"]:checked").length == 1)				        this.checked = true;			            else				            this.checked = false;		           });	             });',true);$Script->method("change","name,value",                'Utility.loading();                 setTimeout(function() { 								Request.setValue(name,value,false);                                Utility.close();                                }, Data.DFT_TM);',true);$Script->method("copy","from,to",                'Request.setValue(to,Request.value(from),false);',true);								$Script->method("getExplode","separator,obj,position",                 'var receive=obj.split(separator), get="";				 if(receive[position]=="true")get=true;				    else if(receive[position]=="false")get=false;					        else get=receive[position];				 return get;',true);$Script->method("width","name,size",                '$("#"+name+"").css("width",""+size+"%");',true);$Script->method("processMask","value",                 'do{value = value.replace(".","");}while(value.indexOf(".") >= 0);				 return value;',true);$Script->method("mask","name",                 '$("#"+name+"").maskMoney({symbol:"COL$", precision:3, thousands:"."});',true);	$Script->method("highLight","name,color,time",                 '$( "#"+name+"" ).animate({backgroundColor: "#"+color+"",color: "#"+Data.CLRMN+"",width: Data.CLRTM}, time );');					$Script->endObject();$Var->release($Script,$Data,$Var);                     ?>