<?require_once '../classes/_Var.php';$Var        =_Var::getInstance();  $Var->__autoload("Script"); $Script=Script::getInstance();$Script->object("Utility");$Script->initialize();$Script->destroyAttribute();$Script->method("date","name",                "$('#'+name+'').attr('readonly', true);                 $('#'+name+'').datepicker({			                                   yearRange: '-80:+3', /* this is the option you're looking for*/                                          showOn: 'button',			                                   buttonImage: 'Imgs/calendar.gif',			                                   buttonImageOnly: true,        																 dateFormat: 'yy-mm-dd',        																 changeYear: true,        																 beforeShow: function(input, inst){                                                      inst.dpDiv.css({marginTop: -input.offsetHeight + 'px', marginLeft: input.offsetWidth + 'px'});                                                      }		                                     });",true);$Script->method("config","",                "$.datepicker.regional['es'] = {                                             closeText: 'Cerrar',                                             prevText: '',                                             nextText: '',                                             currentText: 'Hoy',                                             monthNames: ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'],                                             monthNamesShort: ['Ene','Feb','Mar','Abr', 'May','Jun','Jul','Ago','Sep', 'Oct','Nov','Dic'],                                             dayNames: ['Domingo', 'Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado'],                                             dayNamesShort: ['Dom','Lun','Mar','Mié','Juv','Vie','Sáb'],                                             dayNamesMin: ['Do'